import { useHistory } from 'react-router'
import Button from '../../common/Button'
import Checkbox from '../../common/Checkbox'
import Input from '../../common/Input'
import styles from './styles.module.css'
import api from '../../api'
import { useEffect, useState } from 'react'

const LoginPage = ({ onLogin }) => {

    const history = useHistory();
    const [login, setLogin] = useState();
    const [password, setpassword] = useState();
    const [data, setData] = useState({
        username: "",
        senha: ""
    });

    const handleSubmit = (e) => {
        e.preventDefault()
        const elements = e.target.elements
        const values = {
            email: data.username,
            password: elements.password.value,
            keepConnected: elements.keepConnected.checked
        }
        if (values.email.trim() === "" && values.password.trim() === "") {
            alert("Validação tosca! Põe qqr coisa aí.")
            return
        }
        onLogin()
        history.push('/atendimento');
    }




    useEffect(() => {
        async function login() {
            const resposta = await api.get("")
            setLogin(resposta.data)
            setpassword(resposta.data)
        }
        login();
    }, []);

    return (
        <>
            <section className={styles.section}>
                <div className={styles.pageLeftSide}>
                    <div>
                        <div className={styles.logo}>
                            <img src="/img/logo.png" alt="Logo Mulher Segura" />
                        </div>
                        <p>Para acessar ao nosso site, por favor, realize o login:</p>
                        <form onSubmit={handleSubmit}>
                            {/* {login.map((login) => ( */}
                            <Input label="Email" type="email" id="email" username={data.username} />
                            {/* )) */}
                            {/* } */}
                            {
                                // password.map((password) =>(
                                <Input label="Senha" type="password" id="password" senha={data.senha} />
                                // ))
                            }
                            <div className={styles.options}>
                                <div>
                                    <Checkbox id="keepConnected">
                                        Manter-me conectado
                                    </Checkbox>
                                </div>
                                <div>
                                    <a href="#" className={styles.link}>Esqueceu sua senha?</a>
                                </div>
                            </div>
                            <div>
                                <Button type="submit">
                                    Entrar
                                </Button>
                            </div>
                            <br />
                            <p>
                                Ainda não possui um cadastro? <br />
                                <a href="#" className={styles.link}>Cadastre-se!</a>
                            </p>
                        </form>
                    </div>
                </div>
                <div className={styles.pageRightSide} style={{ backgroundImage: 'url("/img/mulheres.png")' }}>
                    <p>Acima de tudo, seja a heroína da sua vida, e não a vítima.</p>
                </div>
            </section>
        </>
    )

}

export default LoginPage
